DROP TABLE IF EXISTS "USERS";

CREATE TABLE "USERS" (
  "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "USERNAME" VARCHAR(64) NOT NULL,
  "HASH" VARCHAR(64),
  "EMAIL" VARCHAR(256),
  "APIKEY" VARCHAR(64),
  "LOCKED" BOOLEAN DEFAULT 0 NOT NULL,
  "FORCE_PASSWORD_CHANGE" BOOLEAN DEFAULT 0 NOT NULL,
  "EXPIRES" DATE
);

